
public class driver {
  
  static String test = "";

  public static void main(String[] args) {
    Packet packet =  new DATA(3000,
    "localhost",
    9000,
    "This is the payload message..."
    );
    UDPSenderReceiver client = new UDPSenderReceiver();
    UDPSenderReceiver server = new UDPSenderReceiver(9000);
    client.start();
    server.start();
    client.send(3005, packet.getBytes());
    try {
      client.join();
      server.join();
    } catch (Exception e) {
      client.close();
      server.close();
    }


  }
}